   import { Skeleton } from '@/components/Skeleton';

   interface MetricCardProps {
     title: string;
     value?: number;
     trend?: { value: number; direction: 'up' | 'down' };
     format?: 'number' | 'duration' | 'percentage';
   }

   export function MetricCard({ title, value, trend, format = 'number' }: MetricCardProps) {
     const formatValue = (val: number) => {
       switch (format) {
         case 'duration':
           return `${val.toFixed(1)}s`;
         case 'percentage':
           return `${val.toFixed(1)}%`;
         default:
           return val.toLocaleString();
       }
     };

     return (
       <div className="metric-card">
         <div className="metric-title">{title}</div>
         <div className="metric-value">
           {value !== undefined ? formatValue(value) : <Skeleton />}
         </div>
         {trend && (
           <div className={`metric-trend ${trend.direction}`}>
             {trend.direction === 'up' ? '↑' : '↓'} {trend.value}%
           </div>
         )}
       </div>
     );
   }

   export function MetricsGrid({ children, loading }: { children: React.ReactNode; loading: boolean }) {
     return (
       <div className="metrics-grid">
         {children}
       </div>
     );
   }
