   import { useMemo } from 'react';
   import {
     useReactTable,
     getCoreRowModel,
     getSortedRowModel,
     flexRender,
     createColumnHelper,
   } from '@tanstack/react-table';
   import { ChartContainer } from './ChartContainer';

   interface Conversation {
     conversationId: string;
     name: string;
     messageCount: number;
     lastActivity: string;
   }

   const columnHelper = createColumnHelper<Conversation>();

   export function TopConversations({ data, loading }: { data?: Conversation[]; loading: boolean }) {
     const columns = useMemo(() => [
       columnHelper.accessor('name', {
         header: 'Conversation',
         cell: info => (
           <div className="conversation-name">
             {info.getValue() || 'Unnamed'}
           </div>
         ),
       }),
       columnHelper.accessor('messageCount', {
         header: 'Messages',
         cell: info => info.getValue().toLocaleString(),
       }),
       columnHelper.accessor('lastActivity', {
         header: 'Last Activity',
         cell: info => new Date(info.getValue()).toLocaleDateString(),
       }),
     ], []);

     const table = useReactTable({
       data: data || [],
       columns,
       getCoreRowModel: getCoreRowModel(),
       getSortedRowModel: getSortedRowModel(),
     });

     return (
       <ChartContainer title="Top Conversations" loading={loading}>
         <table className="analytics-table">
           <thead>
             {table.getHeaderGroups().map(headerGroup => (
               <tr key={headerGroup.id}>
                 {headerGroup.headers.map(header => (
                   <th key={header.id} onClick={header.column.getToggleSortingHandler()}>
                     {flexRender(header.column.columnDef.header, header.getContext())}
                     {header.column.getIsSorted() && (
                       <span>{header.column.getIsSorted() === 'asc' ? ' ↑' : ' ↓'}</span>
                     )}
                   </th>
                 ))}
               </tr>
             ))}
           </thead>
           <tbody>
             {table.getRowModel().rows.map(row => (
               <tr key={row.id}>
                 {row.getVisibleCells().map(cell => (
                   <td key={cell.id}>
                     {flexRender(cell.column.columnDef.cell, cell.getContext())}
                   </td>
                 ))}
               </tr>
             ))}
           </tbody>
         </table>
       </ChartContainer>
     );
   }
   ```

