   import { api } from '@/lib/api';

   export interface DateRange {
     startDate: Date;
     endDate: Date;
   }

   export const analyticsApi = {
     getDashboard: async () => {
       const response = await api.get('/analytics/dashboard');
       return response.data;
     },

     getMessageVolume: async (range: DateRange) => {
       const response = await api.get('/analytics/messages/volume', {
         params: {
           startDate: range.startDate.toISOString(),
           endDate: range.endDate.toISOString(),
         },
       });
       return response.data;
     },

     getMessageTypes: async (range: DateRange) => {
       const response = await api.get('/analytics/messages/types', {
         params: {
           startDate: range.startDate.toISOString(),
           endDate: range.endDate.toISOString(),
         },
       });
       return response.data;
     },

     getDAU: async (range: DateRange) => {
       const response = await api.get('/analytics/users/dau', {
         params: {
           startDate: range.startDate.toISOString(),
           endDate: range.endDate.toISOString(),
         },
       });
       return response.data;
     },

     getTopConversations: async (limit?: number) => {
       const response = await api.get('/analytics/messages/top-conversations', {
         params: { limit },
       });
       return response.data;
     },

     getTopUsers: async (range: DateRange, limit?: number) => {
       const response = await api.get('/analytics/users/top', {
         params: {
           startDate: range.startDate.toISOString(),
           endDate: range.endDate.toISOString(),
           limit,
         },
       });
       return response.data;
     },
   };
