   import { useQuery } from '@tanstack/react-query';
   import { analyticsApi, DateRange } from '../api/analytics-api';

   export function useDashboardData(range: DateRange) {
     return useQuery({
       queryKey: ['analytics', 'dashboard', range.startDate, range.endDate],
       queryFn: () => analyticsApi.getDashboard(),
       staleTime: 5 * 60 * 1000, // 5 minutes
       refetchOnWindowFocus: false,
     });
   }

   export function useMessageVolume(range: DateRange) {
     return useQuery({
       queryKey: ['analytics', 'messageVolume', range.startDate, range.endDate],
       queryFn: () => analyticsApi.getMessageVolume(range),
       staleTime: 5 * 60 * 1000,
     });
   }

